/*!50003 CREATE DEFINER=`root`@`%` FUNCTION `f_getDistance`(srclgt DOUBLE,srclat DOUBLE,tarlgt DOUBLE,tarlat DOUBLE) RETURNS double
BEGIN  
DECLARE radlat1 DOUBLE DEFAULT 0;
DECLARE radlat2 DOUBLE DEFAULT 0;
DECLARE a DOUBLE DEFAULT 0;
DECLARE radlgt1 DOUBLE DEFAULT 0;
DECLARE radlgt2 DOUBLE DEFAULT 0;
DECLARE b DOUBLE DEFAULT 0;
DECLARE s DOUBLE;

IF (srclgt=0 || srclat=0) THEN 
	RETURN 0;
END IF;

SET radlat1 = srclat * PI() / 180.0;
SET radlat2 = tarlat * PI() / 180.0;
SET a = radlat1 - radlat2;

SET radlgt1 = srclgt * PI() / 180.0;
SET radlgt2 = tarlgt * PI() / 180.0;
SET b = radlgt1 - radlgt2;

SET s = 2 * ASIN(SQRT(POW(SIN(a/2),2) + COS(radlat1)*COS(radlat2)*POW(SIN(b/2),2))) * 6378137;
RETURN s;  
END */$$
